import React from "react";
import BaseLayout from "../../layouts/BaseLayout";
import Charts from "./components/charts/Charts";
import patients from "../../assets/icons/Patients.svg";
import cost from "../../assets/icons/cost.svg";
import staff from "../../assets/icons/staff.svg";
import Appointment from "../../assets/icons/appointment.svg";
import operation from "../../assets/icons/operations.svg";
import revenue from "../../assets/icons/revenue.svg";
import profit from "../../assets/icons/profit.svg";
import Appointments from "./components/appointment/Appointment";
import PatientReview from "./components/PatientsReview/PatientReview";
import QueryListing from "./components/queryListing/QueryListing";
import { useSelector } from "react-redux";

const Dashboard = () => {
  const userType = useSelector((state) => state.setUserType);

  const renderCards = () => {
    if (userType === 1) {
      // If userType is 1, render the following cards
      return (
        <>
          <Card icon={patients} value="500" label="Patients" />
          <Card icon={cost} value="$1200" label="Avg.Cost" />
          <Card icon={staff} value="300" label="Staff Members" />
          <Card icon={Appointment} value="3000" label="Appointment" />
          <Card icon={operation} value="200" label="All Queries" />
        </>
      );
    } else if (userType === 2) {
      // If userType is 2, render the following cards
      return (
        <>
          <Card icon={revenue} value="$50,000" label="Revenue" />
          <Card icon={profit} value="$8,000" label="Today's Profit" />
          <Card icon={Appointment} value="3000" label="Appointment" />
          <Card icon={staff} value="300" label="Staff Members" />
          <Card icon={Appointment} value="150" label="Today's Appointments" />
        </>
      );
    }
  };

  return (
    <BaseLayout>
      <h3 className="mx-5 py-4 font-bold text-gray-700 text-lg">Dashboard</h3>
      <div className="flex space-x-4 mx-5 justify-center row">
        {renderCards()}
      </div>
      <div className="mt-10">
        <Charts />
      </div>
      <div className="flex flex-row mt-10 mb-10">
        <Appointments />
        <PatientReview />
        <QueryListing />
      </div>
    </BaseLayout>
  );
};

// Card component for rendering individual cards
const Card = ({ icon, value, label }) => (
  <div className="rounded-md gap-5 w-[220px] h-[140px] bg-white border border-gray-300 shadow-md flex items-center justify-center">
    <div className="flex items-center space-x-4">
      <img src={icon} alt={label} className="w-[60px] h-[60px] object-cover" />
      <div className="text-left">
        <h4 className="text-xl font-bold text-black">{value}</h4>
        <h4 className="text-lg font-normal text-gray-500">{label}</h4>
      </div>
    </div>
  </div>
);

export default Dashboard;
