import React, { useEffect, useState } from "react";
import axios from "axios";
import "../../../../App.css";

const Appointment = () => {
  const [doctors, setDoctors] = useState([]);

  // Fetch doctor data from the API when the component mounts
  useEffect(() => {
    const fetchDoctors = async () => {
      try {
        const response = await axios.get("http://localhost:8080/public/doctor");
        setDoctors(response.data); // Assuming the API returns an array of doctors
      } catch (error) {
        console.error("Error fetching doctor data:", error);
      }
    };

    fetchDoctors();
  }, []);

  return (
    <div className="flex row space-x-4 mx-5 ">
      <div className="p-6 bg-white rounded-lg shadow-md w-96 mb-10">
        <div className="flex justify-between items-center mb-4 border-b py-3">
          <h2 className="text-lg font-semibold">
            <span role="img" aria-label="calendar" className="mr-2">
              ðŸ“…
            </span>
            Latest Appointments
          </h2>
          <span className="text-gray-500">{doctors.length} Doctors</span>
        </div>
        <ul className="doctor-card-scroll">
          {doctors.map((doctor, index) => (
            <li
              key={index}
              className="flex items-center justify-between py-2  last:border-b-0"
            >
              <div className="flex items-center">
                <img
                  src={doctor.image || "https://via.placeholder.com/50"} // Display image from API or placeholder
                  alt={doctor.name}
                  className="w-10 h-10 rounded-full mr-4"
                />
                <div>
                  <p className="font-medium">{doctor.name}</p>
                  <p className="text-gray-500 text-sm">
                    {doctor.specialty}
                  </p>{" "}
                  {/* Display doctor's specialty */}
                </div>
              </div>
              <div className="flex items-center space-x-2">
                <button className="w-8 h-8 rounded-full bg-green-100 text-green-500">
                  âœ“
                </button>
                <button className="w-8 h-8 rounded-full bg-red-100 text-red-500">
                  âœ•
                </button>
              </div>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Appointment;
