import React, { useState } from "react";
import DatePicker from "react-datepicker";
import Modal from "react-modal";
import "react-datepicker/dist/react-datepicker.css";
import "tailwindcss/tailwind.css";
import "./CalendarWithTimePicker.css";

Modal.setAppElement("#root"); // Required for screen readers, add your app root id

const CalendarWithTimePicker = () => {
  const [selectedDate, setSelectedDate] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [startTime, setStartTime] = useState("");
  const [endTime, setEndTime] = useState("");
  const [selectedTimeRange, setSelectedTimeRange] = useState(null);

  const handleDateChange = (date) => {
    setSelectedDate(date);
    setIsModalOpen(true); // Open time picker modal on date select
  };

  const handleTimeSave = () => {
    if (startTime && endTime) {
      setSelectedTimeRange(`${startTime} - ${endTime}`);
      setIsModalOpen(false); // Close modal after selecting time range
    } else {
      alert("Please select both start and end time");
    }
  };

  return (
    <div className="container mx-auto p-6">
      <h1 className="text-xl font-bold mb-4">Select a Date</h1>

      <DatePicker
        selected={selectedDate}
        onChange={handleDateChange}
        inline
        className="w-full border border-gray-300 p-2 rounded-md"
        id="date"
      />

      {selectedDate && (
        <div className="mt-4">
          <p className="text-lg">
            Selected Date: {selectedDate.toDateString()}
          </p>
          {selectedTimeRange && (
            <p className="text-lg mt-2">
              Selected Time:{" "}
              <span className="font-semibold">{selectedTimeRange}</span>
            </p>
          )}
        </div>
      )}

      {/* Modal for Time Selection */}
      <Modal
        isOpen={isModalOpen}
        onRequestClose={() => setIsModalOpen(false)}
        className="bg-white p-6 rounded-lg max-w-md mx-auto my-20"
        overlayClassName="bg-gray-500 bg-opacity-75 fixed inset-0"
      >
        <h2 className="text-2xl font-semibold mb-4">Select Time Range</h2>
        <div className="mb-4">
          <label className="block mb-2 text-sm font-medium">Start Time:</label>
          <input
            type="time"
            value={startTime}
            onChange={(e) => setStartTime(e.target.value)}
            className="w-full p-2 border border-gray-300 rounded-md"
          />
        </div>
        <div className="mb-4">
          <label className="block mb-2 text-sm font-medium">End Time:</label>
          <input
            type="time"
            value={endTime}
            onChange={(e) => setEndTime(e.target.value)}
            className="w-full p-2 border border-gray-300 rounded-md"
          />
        </div>
        <div className="flex justify-end space-x-2">
          <button
            className="bg-gray-500 text-white py-2 px-4 rounded-md"
            onClick={() => setIsModalOpen(false)}
          >
            Cancel
          </button>
          <button
            className="bg-blue-500 text-white py-2 px-4 rounded-md"
            onClick={handleTimeSave}
          >
            Save Time Range
          </button>
        </div>
      </Modal>
    </div>
  );
};

export default CalendarWithTimePicker;
