import React from "react";
import profile from "../../../../assets/images/google.png";
import bg from "../../../../assets/images/trioakBg.png";

const ProfileCard = ({ patientData }) => {
  return (
    <div className="bg-white p-6 rounded-lg shadow-lg h-[550px] w-[900px]">
      <div className="relative">
        <img
          className="w-full h-32 rounded-t-lg object-cover"
          src={bg}
          alt="Cover"
        />
        <img
          className="absolute left-1/2 transform -translate-x-1/2 -translate-y-1/2 rounded-full border-4 border-white"
          src={profile}
          alt="Profile"
          width={130}
          height={130}
        />
      </div>
      <div className="text-center mt-20">
        <h2 className="text-2xl font-semibold">{patientData.name}</h2>{" "}
        {/* Display patient name */}
        <p className="text-gray-500">{patientData.age} Years old</p>{" "}
        {/* Display patient age */}
        <div className="mt-4">
          <p className="text-sm text-gray-500">Complete your profile</p>
          <div className="w-full bg-gray-200 h-2 rounded-full mt-1">
            <div
              className="bg-blue-500 h-2 rounded-full"
              style={{ width: "89%" }} // You can adjust this based on completion percentage
            ></div>
          </div>
        </div>
        <div className="mt-4 space-y-2 text-left">
          <p>
            <strong>Gender:</strong> {patientData.gender} {/* Display gender */}
          </p>
          <p>
            <strong>Birthday:</strong>{" "}
            {new Date(patientData.birthday).toLocaleDateString()}{" "}
            {/* Display birthday */}
          </p>
          <p>
            <strong>Phone No.:</strong> {patientData.phone}{" "}
            {/* Display phone number */}
          </p>
          <p>
            <strong>Address:</strong> {patientData.address}{" "}
            {/* Display address */}
          </p>
          <p>
            <strong>Blood Group:</strong> {patientData.bloodGroup}{" "}
            {/* Display blood group */}
          </p>
        </div>
      </div>
    </div>
  );
};

export default ProfileCard;
