import React, { useState, useEffect } from "react";
import Modal from "react-modal"; // You need to install react-modal using: npm install react-modal

Modal.setAppElement("#root"); // Set this for accessibility

const AttendanceCalendar = () => {
  const [currentDate, setCurrentDate] = useState(new Date());
  const [dailyAttendance, setDailyAttendance] = useState([]);
  const [apLeave, setApLeave] = useState([]);
  const [selectedDate, setSelectedDate] = useState(null);
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [timeRange, setTimeRange] = useState({ start: "9:00", end: "17:00" });
  const today = new Date();
  const monthNames = [
    "Jan",
    "Feb",
    "Mar",
    "Apr",
    "May",
    "Jun",
    "Jul",
    "Aug",
    "Sep",
    "Oct",
    "Nov",
    "Dec",
  ];

  const populateMonthDropdown = () => {
    return monthNames.map((month, index) => (
      <option key={index} value={index}>
        {month}
      </option>
    ));
  };

  const populateYearDropdown = () => {
    const currentYear = today.getFullYear();
    const previousYears = [currentYear - 2, currentYear - 1, currentYear];
    return previousYears.map((year) => (
      <option key={year} value={year}>
        {year}
      </option>
    ));
  };

  const getAttendanceStatus = (day, month, year) => {
    // Your existing logic for fetching attendance or leave data
    return { status: "Absent", timeIn: "9 am", timeOut: "11 am" };
  };

  const generateCalendar = (month, year) => {
    const firstDay = new Date(year, month).getDay();
    const daysInMonth = new Date(year, month + 1, 0).getDate();
    const prevMonthDays = new Date(year, month, 0).getDate();

    const offset = firstDay === 0 ? 6 : firstDay - 1;

    const dates = [];

    // Previous month's trailing days
    for (let i = offset; i > 0; i--) {
      dates.push(
        <div
          key={`prev-${i}`}
          className="w-1/7 bg-gray-200 text-gray-500 py-2 text-center"
        >
          {prevMonthDays - i + 1}
        </div>
      );
    }

    // Current month's days
    for (let day = 1; day <= daysInMonth; day++) {
      const attendance = getAttendanceStatus(day, month, year);
      const attendanceStatus = attendance?.status || "";
      let eventClass = "";
      let eventText = "";

      if (attendanceStatus === "SL") {
        eventClass = "bg-blue-100 border-l-4 border-blue-500";
        eventText = `Short Leave: ${attendance.timeIn}`;
      } else if (attendanceStatus === "HD") {
        eventClass = "bg-yellow-100 border-l-4 border-yellow-500";
        eventText = `Half Day: ${attendance.timeIn}`;
      } else if (attendanceStatus === "Absent") {
        eventClass = "bg-red-100 border-l-4 border-red-500";
        eventText = `${attendance.timeIn || ""} - ${attendance.timeOut || ""}`;
      } else if (attendanceStatus === "L") {
        eventClass = "bg-green-100 border-l-4 border-green-500";
        eventText = "Leave";
      } else if (attendanceStatus === "P") {
        eventClass = "bg-gray-100 border-l-4 border-gray-500";
        eventText = `${attendance.timeIn || ""} - ${attendance.timeOut || ""}`;
      }

      dates.push(
        <div
          key={day}
          className={`w-1/7 p-2 text-center ${eventClass} cursor-pointer`}
          onClick={() => handleDateClick(day, month, year)}
        >
          <div>{day}</div>
          <div>{eventText}</div>
        </div>
      );
    }

    return dates;
  };

  const handleDateClick = (day, month, year) => {
    setSelectedDate(new Date(year, month, day));
    setModalIsOpen(true);
  };

  const closeModal = () => {
    setModalIsOpen(false);
  };

  const handleTimeChange = (e) => {
    const { name, value } = e.target;
    setTimeRange((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  useEffect(() => {
    // Fetch or set your attendance data here
    setDailyAttendance([]);
    setApLeave([]);
  }, []);

  return (
    <div className="w-full p-4 bg-white rounded-lg shadow">
      <div className="flex justify-between items-center mb-4">
        <h3 className="text-lg font-semibold">Attendance</h3>
        <div className="flex">
          <button
            className="text-lg"
            onClick={() =>
              setCurrentDate(
                new Date(currentDate.setMonth(currentDate.getMonth() - 1))
              )
            }
          >
            &larr;
          </button>
          <button
            className="text-lg ml-2"
            onClick={() =>
              setCurrentDate(
                new Date(currentDate.setMonth(currentDate.getMonth() + 1))
              )
            }
          >
            &rarr;
          </button>
          <select
            className="ml-4 border-none"
            onChange={(e) =>
              setCurrentDate(
                new Date(currentDate.setMonth(parseInt(e.target.value)))
              )
            }
          >
            {populateMonthDropdown()}
          </select>
          <select
            className="ml-2 border-none"
            onChange={(e) =>
              setCurrentDate(
                new Date(currentDate.setFullYear(parseInt(e.target.value)))
              )
            }
          >
            {populateYearDropdown()}
          </select>
        </div>
      </div>

      {/* Calendar */}
      <div className="grid grid-cols-7 gap-4">
        <div className="text-center bg-gray-800 text-white py-2">Mon</div>
        <div className="text-center bg-gray-800 text-white py-2">Tue</div>
        <div className="text-center bg-gray-800 text-white py-2">Wed</div>
        <div className="text-center bg-gray-800 text-white py-2">Thu</div>
        <div className="text-center bg-gray-800 text-white py-2">Fri</div>
        <div className="text-center bg-gray-800 text-white py-2">Sat</div>
        <div className="text-center bg-gray-800 text-white py-2">Sun</div>
      </div>
      <div className="grid grid-cols-7 gap-4 mt-4">
        {generateCalendar(currentDate.getMonth(), currentDate.getFullYear())}
      </div>

      {/* Modal */}
      <Modal isOpen={modalIsOpen} onRequestClose={closeModal} className="modal">
        <div className="p-4 bg-white rounded shadow-lg">
          <h2 className="text-lg mb-4">
            Select Time for {selectedDate?.toLocaleDateString()}
          </h2>
          <div className="flex mb-4">
            <div className="mr-4">
              <label>From: </label>
              <select
                name="start"
                value={timeRange.start}
                onChange={handleTimeChange}
              >
                <option value="9:00">9:00</option>
                <option value="10:00">10:00</option>
                {/* Add more time options */}
              </select>
            </div>
            <div>
              <label>To: </label>
              <select
                name="end"
                value={timeRange.end}
                onChange={handleTimeChange}
              >
                <option value="17:00">17:00</option>
                <option value="18:00">18:00</option>
                {/* Add more time options */}
              </select>
            </div>
          </div>
          <button
            onClick={closeModal}
            className="px-4 py-2 bg-blue-500 text-white"
          >
            Save
          </button>
        </div>
      </Modal>
    </div>
  );
};

export default AttendanceCalendar;
