import React from "react";
import BaseLayout from "../../layouts/BaseLayout";
import Charts from "./components/charts/Charts";
import patients from "../../assets/icons/Patients.svg";
import cost from "../../assets/icons/cost.svg";
import staff from "../../assets/icons/staff.svg";
import Appointment from "../../assets/icons/appointment.svg";
import operation from "../../assets/icons/operations.svg";
import revenue from "../../assets/icons/revenue.svg";
import profit from "../../assets/icons/profit.svg";
import todayAppointment from "../../assets/icons/today-appointment.svg";
import Appointments from "./components/appointment/Appointment";
import PatientReview from "./components/PatientsReview/PatientReview";
import QueryListing from "./components/queryListing/QueryListing";
import { useSelector } from "react-redux";

const Dashboard = () => {
  const userType = useSelector((state) => state.setUserType);
  console.log("..........", userType);

  // Define the card options based on userType
  const cardOptions =
    userType === 1
      ? [
          { icon: patients, label: "Patients", value: 500 },
          { icon: cost, label: "Avg.Cost", value: "$1200" },
          { icon: staff, label: "Staff Members", value: 300 },
          { icon: Appointment, label: "Appointment", value: 3000 },
          { icon: operation, label: "All Queries", value: 200 },
        ]
      : [
          { icon: revenue, label: "Revenue", value: "$8000" },
          { icon: profit, label: "Today's Profit", value: "$1500" },
          { icon: Appointment, label: "Appointment", value: 1500 },
          { icon: staff, label: "Staff Members", value: 100 },
          { icon: todayAppointment, label: "Today's Appointments", value: 50 },
        ];

  return (
    <BaseLayout>
      <h3 className="mx-5 py-4 font-bold text-gray-700 text-lg">Dashboard</h3>
      <div className="flex space-x-4 mx-5 justify-center row">
        {cardOptions.map((card, index) => (
          <div
            key={index}
            className="w-[220px] rounded-md gap-5 h-[140px] bg-white border border-gray-300 shadow-md flex items-center justify-center"
          >
            <div className="flex items-center space-x-4">
              <img
                src={card.icon}
                alt={card.label}
                className="w-[60px] h-[60px] object-cover"
              />
              <div className="text-left">
                <h4 className="text-xl font-bold text-black">{card.value}</h4>
                <h4 className="text-lg font-normal text-gray-500">
                  {card.label}
                </h4>
              </div>
            </div>
          </div>
        ))}
      </div>
      <div className="mt-10">
        <Charts />
      </div>
      <div className="flex flex-row mt-10 mb-10">
        <Appointments />
        <PatientReview />
        <QueryListing />
      </div>
    </BaseLayout>
  );
};

export default Dashboard;
