import React, { useState, useEffect } from "react";
import Modal from "react-modal";
import "tailwindcss/tailwind.css";
import BaseLayout from "../../layouts/BaseLayout";
import axios from "axios"; // Import axios for API calls
import { useSelector } from "react-redux";

Modal.setAppElement("#root");

const AttendanceCalendar = () => {
  const [currentDate, setCurrentDate] = useState(new Date());
  const [dailyAttendance, setDailyAttendance] = useState([]);
  const [apLeave, setApLeave] = useState([]);
  const [selectedDate, setSelectedDate] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [startTime, setStartTime] = useState("09:00");
  const [endTime, setEndTime] = useState("17:00");
  const [timeRanges, setTimeRanges] = useState({}); // To store time ranges for each date
  const [dateTimes, setDateTimes] = useState({});
  const [selectedDateTimes, setSelectedDateTimes] = useState([]); // State to hold time slots for the selected date

  const token = useSelector((state) => state.token);

  const today = new Date();
  const monthNames = [
    "Jan",
    "Feb",
    "Mar",
    "Apr",
    "May",
    "Jun",
    "Jul",
    "Aug",
    "Sep",
    "Oct",
    "Nov",
    "Dec",
  ];

  const populateMonthDropdown = () => {
    return monthNames.map((month, index) => (
      <option key={index} value={index}>
        {month}
      </option>
    ));
  };

  const populateYearDropdown = () => {
    const currentYear = today.getFullYear();
    const previousYears = [
      currentYear - 2,
      currentYear - 1,
      currentYear,
      currentYear + 1,
      currentYear + 2,
      currentYear + 3,
      currentYear + 4,
      currentYear + 5,
    ];
    return previousYears.map((year) => (
      <option key={year} value={year}>
        {year}
      </option>
    ));
  };

  useEffect(() => {
    const fetchCheckupDates = async () => {
      try {
        const response = await axios.get("http://localhost:8080/doctor/dates", {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        if (
          response.data.success &&
          Array.isArray(response.data.datesAndTimes)
        ) {
          const fetchedData = {};

          response.data.datesAndTimes.forEach((item) => {
            const dateKey = new Date(item.date).toISOString().split("T")[0];

            const timeRanges = item.timeRanges.map((timeRange) => ({
              startTime: timeRange.startTime,
              endTime: timeRange.endTime,
            }));

            const checkupTimes = item.checkupTimes || [];
            const checkupTimeRanges = checkupTimes.map((checkup) => ({
              startTime: checkup.startTime,
              endTime: checkup.endTime,
            }));

            // Store both timeRanges and checkupTimeRanges under the date key
            fetchedData[dateKey] = [...timeRanges, ...checkupTimeRanges];
          });

          setDateTimes(fetchedData); // Update state with fetched data

          // Log the fetchedData directly instead of dateTimes

          // Now you can log the start and end times as well
          Object.keys(fetchedData).forEach((date) => {
            console.log("date:", date);
            fetchedData[date].forEach((timeSlot) => {
              console.log("Start Time:", timeSlot.startTime);
              console.log("End Time:", timeSlot.endTime);
            });
          });
          console.log("Fetched Data: ", fetchedData);
        } else {
          console.error("Unexpected response structure");
        }
      } catch (error) {
        console.error("Error fetching dates: ", error);
      }
    };

    fetchCheckupDates();
  }, [token]);

  const getAttendanceStatus = (day, month, year) => {
    return { status: "Absent" };
  };

  const generateCalendar = (month, year) => {
    const firstDay = new Date(year, month).getDay();
    const daysInMonth = new Date(year, month + 1, 0).getDate();
    const prevMonthDays = new Date(year, month, 0).getDate();

    const offset = firstDay === 0 ? 6 : firstDay - 1;
    const dates = [];

    // Previous month's days
    for (let i = offset; i > 0; i--) {
      dates.push(
        <div
          key={`prev-${i}`}
          className="w-1/7 bg-gray-200 text-gray-500 py-2 text-center"
        >
          {prevMonthDays - i + 1}
        </div>
      );
    }

    // Current month's days
    for (let day = 1; day <= daysInMonth; day++) {
      const dateKey = `${year}-${month + 1}-${day}`; // Adjust month to be 1-indexed
      const hasTimeRanges = dateTimes[dateKey];

      const eventClass = hasTimeRanges
        ? "bg-green-100 border-l-4 border-green-500"
        : "bg-red-100 border-l-4 border-red-500";

      // Display all time ranges if they exist
      const timeText = hasTimeRanges
        ? hasTimeRanges
            .map((range) => `${range.startTime} - ${range.endTime}`)
            .join(", ")
        : "";

      dates.push(
        <div
          key={day}
          className={`w-1/7 p-2 text-center h-[100px] flex flex-col justify-center ${eventClass} cursor-pointer`}
          onClick={() => handleDateClick(day, month, year)}
        >
          <div>{day}</div>
          {hasTimeRanges && <div className="text-sm">{timeText}</div>}{" "}
          {/* Display time ranges */}
        </div>
      );
    }

    return dates;
  };

  const handleDateClick = (day, month, year) => {
    const dateKey = `${year}-${month + 1}-${day}`; // Adjust month to be 1-indexed
    setSelectedDate(new Date(year, month, day));
    setSelectedDateTimes(dateTimes[dateKey] || []); // Get time slots for the selected date
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
  };

  const handleTimeSave = async () => {
    if (startTime && endTime && selectedDate) {
      const dateKey = `${selectedDate.getFullYear()}-${
        selectedDate.getMonth() + 1
      }-${selectedDate.getDate()}`; // Adjust month to be 1-indexed

      const payload = {
        date: selectedDate.toISOString().split("T")[0],
        timeRanges: [{ startTime, endTime }],
      };

      try {
        const response = await axios.post(
          "http://localhost:8080/doctor/set-checkup-times",
          payload,
          {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          }
        );
        console.log("API response: ", response.data);

        // Update local state with the time range for selected date
        setTimeRanges((prevRanges) => ({
          ...prevRanges,
          [dateKey]: [{ startTime, endTime }], // Store as an array
        }));

        // Update the selectedDateTimes to include the new time range
        setSelectedDateTimes((prevTimes) => [
          ...prevTimes,
          { startTime, endTime },
        ]);

        setIsModalOpen(false); // Close modal after saving
      } catch (error) {
        console.error(
          "Error setting checkup time: ",
          error.response ? error.response.data : error
        );
        alert(
          error.response?.data.message ||
            "Failed to save checkup time. Please try again."
        );
      }
    } else {
      alert("Please select both start and end time");
    }
  };

  useEffect(() => {
    setDailyAttendance([]);
    setApLeave([]);
  }, []);

  const handleMonthChange = (e) => {
    setCurrentDate(
      new Date(currentDate.getFullYear(), parseInt(e.target.value), 1)
    );
  };

  const handleYearChange = (e) => {
    setCurrentDate(
      new Date(parseInt(e.target.value), currentDate.getMonth(), 1)
    );
  };

  return (
    <BaseLayout>
      <div className="w-full p-4 bg-white rounded-lg shadow">
        <div className="flex justify-between items-center mb-4">
          <h2 className="text-xl font-semibold">Attendance Calendar</h2>
          <div>
            <select
              onChange={handleMonthChange}
              value={currentDate.getMonth()}
              className="border border-gray-300 rounded-md p-1 mr-2"
            >
              {populateMonthDropdown()}
            </select>
            <select
              onChange={handleYearChange}
              value={currentDate.getFullYear()}
              className="border border-gray-300 rounded-md p-1"
            >
              {populateYearDropdown()}
            </select>
          </div>
        </div>

        <div className="grid grid-cols-7 gap-2">
          {generateCalendar(currentDate.getMonth(), currentDate.getFullYear())}
        </div>

        <Modal
          isOpen={isModalOpen}
          onRequestClose={closeModal}
          className="w-1/2 mx-auto p-4 bg-white rounded-lg shadow-lg"
        >
          <h2 className="text-lg font-semibold">Select Time</h2>
          <div className="flex flex-col mt-4">
            <label className="mb-2">Start Time:</label>
            <input
              type="time"
              value={startTime}
              onChange={(e) => setStartTime(e.target.value)}
              className="border border-gray-300 rounded-md p-1 mb-4"
            />
            <label className="mb-2">End Time:</label>
            <input
              type="time"
              value={endTime}
              onChange={(e) => setEndTime(e.target.value)}
              className="border border-gray-300 rounded-md p-1 mb-4"
            />
            <button
              onClick={handleTimeSave}
              className="bg-blue-500 text-white p-2 rounded-md"
            >
              Save Time
            </button>
          </div>

          {selectedDateTimes.length > 0 && (
            <div className="mt-4">
              <h3 className="text-lg font-semibold">Available Time Slots:</h3>
              <ul className="list-disc ml-4">
                {selectedDateTimes.map((time, index) => (
                  <li key={index}>
                    {time.startTime} - {time.endTime}
                  </li>
                ))}
              </ul>
            </div>
          )}
        </Modal>
      </div>
    </BaseLayout>
  );
};

export default AttendanceCalendar;
