import React, { useState } from "react";
import axios from "axios";
import BaseLayout from "../../../layouts/BaseLayout";
import "./AddStaff.css";

const AddStaff = () => {
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    email: "",
    phone: "",
    department: "Nurse",
    gender: "Male",
    bio: "",
  });
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");

  // Handle form input change
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post(
        "http://localhost:8080/staff/add",
        formData
      );
      setSuccess(response.data.message); // Assuming the success message is in response.data.message
      setError(""); // Clear error message
      // Clear form data
      setFormData({
        firstName: "",
        lastName: "",
        email: "",
        phone: "",
        department: "Nurse",
        gender: "Male",
        bio: "",
      });
    } catch (err) {
      setError(err.response.data.message || "Something went wrong."); // Handle error response
      setSuccess(""); // Clear success message
    }
  };

  return (
    <BaseLayout>
      <div className="min-h-screen bg-gray-50 mb-16">
        {/* Header */}
        <div className="mx-5 flex justify-between">
          <h3 className="py-2 font-bold text-gray-700 text-lg">
            Complete The Profile
          </h3>
        </div>

        {/* Main Content */}
        <div className="flex justify-center">
          {/* Form Container */}
          <div className="bg-white shadow-md rounded-lg p-6 w-full max-w-4xl">
            {/* Display Error Message */}
            {error && <p className="text-red-500">{error}</p>}
            {/* Display Success Message */}
            {success && <p className="text-green-500">{success}</p>}

            <div className="flex items-center mb-8">
              <input id="imgUpload" type="file" hidden />
              <label htmlFor="imgUpload" className="cursor-pointer">
                <img
                  src="https://via.placeholder.com/600"
                  alt="Profile"
                  className="w-24 h-24 rounded-full mr-6 hover:opacity-80"
                />
              </label>
              <div className="flex flex-col">
                <h2 className="text-xl font-bold">Upload your picture</h2>
                <span className="text-sm text-gray-500">
                  For best results, use an image at least 600px by 600px in
                  either .jpg or .png format
                </span>
                <div className="flex space-x-4 mt-4">
                  <button className="bg-blue-500 text-white px-4 py-2 rounded-md hover:bg-blue-600">
                    Upload
                  </button>
                  <button className="bg-gray-100 text-gray-600 px-4 py-2 rounded-md hover:bg-gray-200">
                    Remove
                  </button>
                </div>
              </div>
            </div>

            <form onSubmit={handleSubmit}>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                <div>
                  <label className="block text-gray-600">First Name</label>
                  <input
                    type="text"
                    name="firstName"
                    value={formData.firstName}
                    onChange={handleChange}
                    placeholder="First Name"
                    className="w-full mt-2 p-2 border rounded"
                  />
                </div>
                <div>
                  <label className="block text-gray-600">Last Name</label>
                  <input
                    type="text"
                    name="lastName"
                    value={formData.lastName}
                    onChange={handleChange}
                    placeholder="Last Name"
                    className="w-full mt-2 p-2 border rounded"
                  />
                </div>
                <div>
                  <label className="block text-gray-600">Your Email</label>
                  <input
                    type="email"
                    name="email"
                    value={formData.email}
                    onChange={handleChange}
                    placeholder="Your email"
                    className="w-full mt-2 p-2 border rounded"
                  />
                </div>
                <div>
                  <label className="block text-gray-600">Phone no.</label>
                  <input
                    type="text"
                    name="phone"
                    value={formData.phone}
                    onChange={handleChange}
                    placeholder="Phone no."
                    className="w-full mt-2 p-2 border rounded"
                  />
                </div>
                <div>
                  <label className="block text-gray-600">Departments</label>
                  <select
                    name="department"
                    value={formData.department}
                    onChange={handleChange}
                    className="w-full mt-2 p-2 border rounded"
                  >
                    <option>Nurse</option>
                    <option>Medical technologists</option>
                    <option>Pharmacist</option>
                    <option>Social Worker</option>
                    <option>Patient Advocates</option>
                  </select>
                </div>
                <div>
                  <label className="block text-gray-600">Gender</label>
                  <select
                    name="gender"
                    value={formData.gender}
                    onChange={handleChange}
                    className="w-full mt-2 p-2 border rounded"
                  >
                    <option>Male</option>
                    <option>Female</option>
                    <option>Other</option>
                  </select>
                </div>
              </div>
              <div className="mt-6">
                <label className="block text-gray-600">Your Bio Here</label>
                <textarea
                  name="bio"
                  value={formData.bio}
                  onChange={handleChange}
                  placeholder="Your Bio Here"
                  className="w-full mt-2 p-2 border rounded"
                  rows="4"
                ></textarea>
              </div>
              <button
                type="submit"
                className="bg-blue-500 text-white py-2 px-4 rounded hover:bg-blue-600 mt-4"
              >
                Add Staff
              </button>
            </form>
          </div>
        </div>
      </div>
    </BaseLayout>
  );
};

export default AddStaff;
