import React, { useState, useEffect } from "react";
import ReactApexChart from "react-apexcharts";
import { ChevronDownIcon } from "@heroicons/react/20/solid";
import { useSelector } from "react-redux";
import axios from "axios";

const yearOptions = [2022, 2021];

const RoundedChart = () => {
  const [isOpen, setIsOpen] = useState(false);
  const userType = useSelector((state) => state.setUserType);
  const userId = useSelector((state) => state.setUserId);

  const [chartOptions, setChartOptions] = useState({
    series: [0, 0, 0], // Initialize with zeros
    options: {
      chart: {
        height: 400,
        type: "radialBar",
      },
      plotOptions: {
        radialBar: {
          dataLabels: {
            name: {
              fontSize: "22px",
            },
            value: {
              fontSize: "16px",
            },
            total: {
              show: true,
              label: "Total",
              formatter: function (w) {
                // Total percentage count
                const total = w.globals.seriesTotals.reduce((a, b) => a + b, 0);
                return total;
              },
            },
          },
        },
      },
      labels: ["Male", "Female", "Others"],
    },
  });

  // Fetch appointments based on userType
  const fetchAppointments = async () => {
    try {
      let url = "";
      if (userType === 1) {
        url =
          "https://consultant-backend-jiwv.onrender.com/appointment/all-appointments";
      } else if (userType === 2) {
        url = `https://consultant-backend-jiwv.onrender.com/doctor/${userId}/appointments`;
      }

      if (url) {
        const response = await axios.get(url);
        const appointments = response.data; // Assuming this is the array of appointments

        // Calculate gender distribution
        const genderData = { male: 0, female: 0, others: 0 };

        appointments.forEach((appointment) => {
          const gender = appointment.gender?.toLowerCase(); // Assuming gender field exists
          if (gender === "male") {
            genderData.male++;
          } else if (gender === "female") {
            genderData.female++;
          } else {
            genderData.others++;
          }
        });

        // Update chart series with new data
        setChartOptions((prev) => ({
          ...prev,
          series: [genderData.male, genderData.female, genderData.others],
        }));
      }
    } catch (error) {
      console.error("Error fetching appointments:", error);
    }
  };

  useEffect(() => {
    fetchAppointments(); // Fetch appointments when the component mounts
  }, [userType, userId]);

  const toggleDropdown = () => {
    setIsOpen(!isOpen);
  };

  const handleSelect = (year) => {
    setIsOpen(false);
    // Additional logic can be added if necessary for year selection
  };

  return (
    <div className="my-8">
      <div className="flex justify-between mx-3">
        <h3 className="text-lg font-bold items-center justify-center flex">
          Patients visit by Gender
        </h3>
        <div className="custom-dropdown">
          <div
            role="button"
            onClick={toggleDropdown}
            className="px-3 py-2 light-bg-L border border-[#667fd1] head-6 green-H flex items-center gap-2 rounded-lg"
          >
            <p>Year</p>
            <ChevronDownIcon className="w-5 h-5" />
          </div>

          {isOpen && (
            <div
              className="dropdown-list-container dropdown-end light-bg-L dark-M body-N p-2 shadow rounded-box"
              style={{ width: "140px" }}
            >
              <ul className="dropdown-list">
                {yearOptions?.map((el, idx) => (
                  <li
                    role="button"
                    key={idx}
                    onClick={() => handleSelect(el)}
                    className="cursor-pointer hover:bg-gray-200 p-2 rounded"
                  >
                    {el}
                  </li>
                ))}
              </ul>
            </div>
          )}
        </div>
      </div>
      <div id="chart">
        <ReactApexChart
          options={chartOptions.options}
          series={chartOptions.series}
          type="radialBar"
          height={400}
        />
      </div>
      <div id="html-dist"></div>
    </div>
  );
};

export default RoundedChart;
