import React, { useState, useEffect } from "react";

const Calendar = () => {
  const [currentDate, setCurrentDate] = useState(new Date());
  const [monthDropdown, setMonthDropdown] = useState([]);
  const [yearDropdown, setYearDropdown] = useState([]);
  const [dates, setDates] = useState([]);

  const monthNames = [
    "Jan",
    "Feb",
    "Mar",
    "Apr",
    "May",
    "Jun",
    "Jul",
    "Aug",
    "Sep",
    "Oct",
    "Nov",
    "Dec",
  ];

  // Static data to replace backend fetching
  const dailyattendance = []; // Example: [{ attendance_date: '2024-09-15', attendance_status: 'P', time_in: '08:00', time_out: '16:00' }]
  const apleave = []; // Example: [{ date_from: '2024-09-10', date_to: '2024-09-12', leavetype: 'L' }]

  useEffect(() => {
    populateMonthDropdown();
    populateYearDropdown();
    generateCalendar(currentDate.getMonth(), currentDate.getFullYear());
  }, [currentDate]);

  const populateMonthDropdown = () => {
    setMonthDropdown(
      monthNames.map((month, index) => (
        <option key={index} value={index}>
          {month}
        </option>
      ))
    );
  };

  const populateYearDropdown = () => {
    const currentYear = new Date().getFullYear();
    const previousYear = currentYear - 1;
    const previousYearback = currentYear - 2;

    setYearDropdown(
      [previousYearback, previousYear, currentYear].map((year) => (
        <option key={year} value={year}>
          {year}
        </option>
      ))
    );
  };

  const getAttendanceStatus = (day, month, year) => {
    for (let i = 0; i < dailyattendance.length; i++) {
      const attendance = dailyattendance[i];
      const attendanceDate = new Date(attendance.attendance_date);

      if (
        attendanceDate.getDate() === day &&
        attendanceDate.getMonth() === month &&
        attendanceDate.getFullYear() === year
      ) {
        return {
          status: attendance.attendance_status,
          timeIn: attendance.time_in,
          timeOut: attendance.time_out,
        };
      }
    }

    for (let j = 0; j < apleave.length; j++) {
      const leave = apleave[j];
      const leaveDateFrom = new Date(leave.date_from);
      const leaveDateTo = new Date(leave.date_to);

      if (
        leaveDateFrom.getDate() <= day &&
        leaveDateTo.getDate() >= day &&
        leaveDateFrom.getMonth() === month &&
        leaveDateFrom.getFullYear() === year
      ) {
        return {
          status: leave.leavetype,
        };
      }
    }

    return {
      status: "",
      timeIn: "",
      timeOut: "",
    };
  };

  const generateCalendar = (month, year) => {
    const datesArray = [];
    const firstDay = new Date(year, month).getDay();
    const daysInMonth = new Date(year, month + 1, 0).getDate();
    const prevMonthDays = new Date(year, month, 0).getDate();

    // Adjusting for Monday start
    const offset = firstDay === 0 ? 6 : firstDay - 1;

    for (let i = offset; i > 0; i--) {
      datesArray.push(
        <div key={`prev-${i}`} className="date empty">
          {prevMonthDays - i + 1}
        </div>
      );
    }

    for (let day = 1; day <= daysInMonth; day++) {
      let eventClass = "";
      let eventText = "";
      const date = new Date(year, month, day);
      const attendance = getAttendanceStatus(day, month, year);
      const attendanceStatus = attendance.status;
      const timeIn = attendance.timeIn;
      const timeOut = attendance.timeOut;

      if (date.getDay() === 6 || date.getDay() === 0) {
        eventClass = "frozen";
      }

      if (attendanceStatus === "SL") {
        eventClass = "sort-leave";
        eventText = `${formatTimeWithAmPm(timeIn)} Short Leave`;
      } else if (attendanceStatus === "HD") {
        eventClass = "half-day";
        eventText = `${formatTimeWithAmPm(timeIn)} Half Day`;
      } else if (attendanceStatus === "") {
        eventClass = "absent";
        eventText = "Absent";
      } else if (attendanceStatus === "L") {
        eventClass = "leave";
        eventText = "Leave";
      } else if (attendanceStatus === "P") {
        eventClass = "present";
        eventText = `${formatTimeWithAmPm(timeIn)} ${formatTimeOut(timeOut)}`;
      }

      datesArray.push(
        <div key={day} className={`date ${eventClass}`}>
          <div className="day">{day}</div>
          <div className="event">{eventText}</div>
        </div>
      );
    }

    setDates(datesArray);
  };

  const formatTimeWithAmPm = (timeIn) => {
    const [hours, minutes] = timeIn.split(":").map(Number);
    const period = hours >= 12 ? "pm" : "am";
    const hours12 = hours % 12 || 12;
    return `${hours12.toString().padStart(2, "0")}:${minutes
      .toString()
      .padStart(2, "0")} ${period}`;
  };

  const formatTimeOut = (timeOut) => {
    if (!timeOut || timeOut.trim() === "") {
      return "";
    } else {
      const [hours, minutes] = timeOut.split(":").map(Number);
      const period = hours >= 12 ? "pm" : "am";
      const hours12 = hours % 12 || 12;
      return `${hours12.toString().padStart(2, "0")}:${minutes
        .toString()
        .padStart(2, "0")} ${period}`;
    }
  };

  const handlePrevMonth = () => {
    setCurrentDate(new Date(currentDate.setMonth(currentDate.getMonth() - 1)));
  };

  const handleNextMonth = () => {
    setCurrentDate(new Date(currentDate.setMonth(currentDate.getMonth() + 1)));
  };

  const handleMonthChange = (event) => {
    const newMonth = parseInt(event.target.value);
    setCurrentDate(new Date(currentDate.setMonth(newMonth)));
  };

  const handleYearChange = (event) => {
    const newYear = parseInt(event.target.value);
    setCurrentDate(new Date(currentDate.setFullYear(newYear)));
  };

  return (
    <div className="calendar">
      <div className="header">
        <h3>Attendance</h3>
        <div>
          <button onClick={handlePrevMonth}>&larr;</button>
          <button onClick={handleNextMonth}>&rarr;</button>
          <select onChange={handleMonthChange} value={currentDate.getMonth()}>
            {monthDropdown}
          </select>
          <select onChange={handleYearChange} value={currentDate.getFullYear()}>
            {yearDropdown}
          </select>
        </div>
      </div>
      <div className="days">
        <div className="day-header">Mon</div>
        <div className="day-header">Tue</div>
        <div className="day-header">Wed</div>
        <div className="day-header">Thu</div>
        <div className="day-header">Fri</div>
        <div className="day-header">Sat</div>
        <div className="day-header">Sun</div>
      </div>
      <div className="dates">{dates}</div>
    </div>
  );
};

export default Calendar;
