import React, { useState, useEffect } from "react";
import Modal from "react-modal";
import TimeRangePicker from "@wojtekmaj/react-timerange-picker";
// import "react-time-range-picker/dist/styles.css"; // Import CSS for the time range picker

const AttendanceCalendar = () => {
  const [currentDate, setCurrentDate] = useState(new Date());
  const [dailyAttendance, setDailyAttendance] = useState([]);
  const [apLeave, setApLeave] = useState([]);
  const [isModalOpen, setIsModalOpen] = useState(false); // Modal state
  const [selectedDate, setSelectedDate] = useState(null); // Selected date state
  const [timeRange, setTimeRange] = useState(["09:00", "11:00"]); // Time range state

  const today = new Date();
  const monthNames = [
    "Jan",
    "Feb",
    "Mar",
    "Apr",
    "May",
    "Jun",
    "Jul",
    "Aug",
    "Sep",
    "Oct",
    "Nov",
    "Dec",
  ];

  const populateMonthDropdown = () => {
    return monthNames.map((month, index) => (
      <option key={index} value={index}>
        {month}
      </option>
    ));
  };

  const populateYearDropdown = () => {
    const currentYear = today.getFullYear();
    const previousYears = [currentYear - 2, currentYear - 1, currentYear];
    return previousYears.map((year) => (
      <option key={year} value={year}>
        {year}
      </option>
    ));
  };

  const getAttendanceStatus = (day, month, year) => {
    for (let i = 0; i < dailyAttendance.length; i++) {
      const attendance = dailyAttendance[i];
      const attendanceDate = new Date(attendance.attendance_date);

      if (
        attendanceDate.getDate() === day &&
        attendanceDate.getMonth() === month &&
        attendanceDate.getFullYear() === year
      ) {
        return {
          status: attendance.attendance_status,
          timeIn: attendance.time_in,
          timeOut: attendance.time_out,
        };
      } else {
        for (let j = 0; j < apLeave.length; j++) {
          const leave = apLeave[j];
          const leaveDateFrom = new Date(leave.date_from);
          const leaveDateTo = new Date(leave.date_to);

          if (
            leaveDateFrom.getDate() <= day &&
            leaveDateTo.getDate() >= day &&
            leaveDateFrom.getMonth() === month &&
            leaveDateFrom.getFullYear() === year
          ) {
            return { status: leave.leavetype };
          }
        }
      }
    }
    return { status: "", timeIn: "", timeOut: "" };
  };

  const generateCalendar = (month, year) => {
    const firstDay = new Date(year, month).getDay();
    const daysInMonth = new Date(year, month + 1, 0).getDate();
    const prevMonthDays = new Date(year, month, 0).getDate();

    const offset = firstDay === 0 ? 6 : firstDay - 1;

    const dates = [];

    for (let i = offset; i > 0; i--) {
      dates.push(
        <div
          key={`prev-${i}`}
          className="w-1/7 bg-gray-200 text-gray-500 py-2 text-center"
        >
          {prevMonthDays - i + 1}
        </div>
      );
    }

    for (let day = 1; day <= daysInMonth; day++) {
      const attendance = getAttendanceStatus(day, month, year);
      const attendanceStatus = attendance?.status || "";
      let eventClass = "";
      let eventText = "";

      if (attendanceStatus === "SL") {
        eventClass = "bg-blue-100 border-l-4 border-blue-500";
        eventText = attendance.timeIn
          ? `Short Leave: ${attendance.timeIn}`
          : "Short Leave";
      } else if (attendanceStatus === "HD") {
        eventClass = "bg-yellow-100 border-l-4 border-yellow-500";
        eventText = attendance.timeIn
          ? `Half Day: ${attendance.timeIn}`
          : "Half Day";
      } else if (attendanceStatus === "") {
        eventClass = "bg-red-100 border-l-4 border-red-500";
        eventText = "Absent";
      } else if (attendanceStatus === "L") {
        eventClass = "bg-green-100 border-l-4 border-green-500";
        eventText = "Leave";
      } else if (attendanceStatus === "P") {
        eventClass = "bg-gray-100 border-l-4 border-gray-500";
        eventText = `${attendance.timeIn || ""} - ${attendance.timeOut || ""}`;
      }

      dates.push(
        <div
          key={day}
          className={`w-1/7 p-2 text-center ${eventClass}`}
          onClick={() => openModal(day, month, year)}
        >
          <div>{day}</div>
          <div>{eventText}</div>
        </div>
      );
    }

    return dates;
  };

  // Handle modal open
  const openModal = (day, month, year) => {
    const date = new Date(year, month, day);
    setSelectedDate(date);
    setIsModalOpen(true);
  };

  // Handle modal close
  const closeModal = () => {
    setIsModalOpen(false);
    setSelectedDate(null);
  };

  // Save time range and update attendance
  const saveTimeRange = () => {
    setDailyAttendance((prev) => [
      ...prev,
      {
        attendance_date: selectedDate,
        attendance_status: "P",
        time_in: timeRange[0],
        time_out: timeRange[1],
      },
    ]);
    closeModal();
  };

  useEffect(() => {
    // Fetch or set your attendance data here
    setDailyAttendance([]);
    setApLeave([]);
  }, []);

  return (
    <div className="w-full p-4 bg-white rounded-lg shadow">
      <div className="flex justify-between items-center mb-4">
        <h3 className="text-lg font-semibold">Attendance</h3>
        <div className="flex">
          <button
            className="text-lg"
            onClick={() =>
              setCurrentDate(
                new Date(currentDate.setMonth(currentDate.getMonth() - 1))
              )
            }
          >
            &larr;
          </button>
          <button
            className="text-lg ml-2"
            onClick={() =>
              setCurrentDate(
                new Date(currentDate.setMonth(currentDate.getMonth() + 1))
              )
            }
          >
            &rarr;
          </button>
          <select
            className="ml-4 border-none"
            onChange={(e) =>
              setCurrentDate(
                new Date(currentDate.setMonth(parseInt(e.target.value)))
              )
            }
          >
            {populateMonthDropdown()}
          </select>
          <select
            className="ml-2 border-none"
            onChange={(e) =>
              setCurrentDate(
                new Date(currentDate.setFullYear(parseInt(e.target.value)))
              )
            }
          >
            {populateYearDropdown()}
          </select>
        </div>
      </div>
      <div className="grid grid-cols-7 gap-4">
        <div className="text-center bg-gray-800 text-white py-2">Mon</div>
        <div className="text-center bg-gray-800 text-white py-2">Tue</div>
        <div className="text-center bg-gray-800 text-white py-2">Wed</div>
        <div className="text-center bg-gray-800 text-white py-2">Thu</div>
        <div className="text-center bg-gray-800 text-white py-2">Fri</div>
        <div className="text-center bg-gray-800 text-white py-2">Sat</div>
        <div className="text-center bg-gray-800 text-white py-2">Sun</div>
      </div>
      <div className="grid grid-cols-7 gap-4 mt-4">
        {generateCalendar(currentDate.getMonth(), currentDate.getFullYear())}
      </div>

      {/* Modal for Time Range Picker */}
      <Modal
        isOpen={isModalOpen}
        onRequestClose={closeModal}
        contentLabel="Time Range Picker Modal"
      >
        <h2>Select Time Range</h2>
        <TimeRangePicker onChange={setTimeRange} value={timeRange} />
        <button
          onClick={saveTimeRange}
          className="mt-4 bg-blue-500 text-white p-2 rounded"
        >
          Save
        </button>
        <button
          onClick={closeModal}
          className="mt-4 bg-red-500 text-white p-2 rounded"
        >
          Close
        </button>
      </Modal>
    </div>
  );
};

export default AttendanceCalendar;
