import React, { useState } from "react";
import BaseLayout from "../../../layouts/BaseLayout";
import axios from "axios";
import "./AddStaff.css";

const AddStaff = () => {
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    email: "",
    phone: "",
    department: "",
    gender: "",
    bio: "",
  });

  const [errorMessage, setErrorMessage] = useState("");
  const [successMessage, setSuccessMessage] = useState("");

  // Handle input change
  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post(
        "http://localhost:8080/staff/add",
        formData
      );

      if (response.data.success) {
        setSuccessMessage("Staff added successfully!");
        setErrorMessage("");
        // Clear form fields after success
        setFormData({
          firstName: "",
          lastName: "",
          email: "",
          phone: "",
          department: "",
          gender: "",
          bio: "",
        });
      } else {
        setSuccessMessage("");
        setErrorMessage(response.data.message || "Failed to add staff.");
      }
    } catch (error) {
      setSuccessMessage("");
      setErrorMessage(error.response?.data?.message || "An error occurred.");
    }
  };

  return (
    <BaseLayout>
      <div className="min-h-screen bg-gray-50 mb-16">
        {/* Header */}
        <div className="mx-5 flex justify-between">
          <h3 className="py-2 font-bold text-gray-700 text-lg">
            Complete The Profile
          </h3>
        </div>

        {/* Main Content */}
        <div className="flex justify-center">
          {/* Form Container */}
          <div className="bg-white shadow-md rounded-lg p-6 w-full max-w-4xl">
            <form onSubmit={handleSubmit}>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                <div>
                  <label className="block text-gray-600">First Name</label>
                  <input
                    type="text"
                    name="firstName"
                    value={formData.firstName}
                    onChange={handleChange}
                    placeholder="First Name"
                    className="w-full mt-2 p-2 border rounded"
                  />
                </div>
                <div>
                  <label className="block text-gray-600">Last Name</label>
                  <input
                    type="text"
                    name="lastName"
                    value={formData.lastName}
                    onChange={handleChange}
                    placeholder="Last Name"
                    className="w-full mt-2 p-2 border rounded"
                  />
                </div>
                <div>
                  <label className="block text-gray-600">Your Email</label>
                  <input
                    type="email"
                    name="email"
                    value={formData.email}
                    onChange={handleChange}
                    placeholder="Your email"
                    className="w-full mt-2 p-2 border rounded"
                  />
                </div>
                <div>
                  <label className="block text-gray-600">Phone no.</label>
                  <input
                    type="text"
                    name="phone"
                    value={formData.phone}
                    onChange={handleChange}
                    placeholder="Phone no."
                    className="w-full mt-2 p-2 border rounded"
                  />
                </div>
                <div>
                  <label className="block text-gray-600">Departments</label>
                  <select
                    name="department"
                    value={formData.department}
                    onChange={handleChange}
                    className="w-full mt-2 p-2 border rounded"
                  >
                    <option value="">Select Department</option>
                    <option value="Nurse">Nurse</option>
                    <option value="Medical technologists">
                      Medical technologists
                    </option>
                    <option value="Pharmacist">Pharmacist</option>
                    <option value="Social Worker">Social Worker</option>
                    <option value="Patient Advocates">Patient Advocates</option>
                  </select>
                </div>
                <div>
                  <label className="block text-gray-600">Gender</label>
                  <select
                    name="gender"
                    value={formData.gender}
                    onChange={handleChange}
                    className="w-full mt-2 p-2 border rounded"
                  >
                    <option value="">Select Gender</option>
                    <option value="Male">Male</option>
                    <option value="Female">Female</option>
                    <option value="Other">Other</option>
                  </select>
                </div>
              </div>

              <div className="mt-6">
                <label className="block text-gray-600">Your Bio Here</label>
                <textarea
                  name="bio"
                  value={formData.bio}
                  onChange={handleChange}
                  placeholder="Your Bio Here"
                  className="w-full mt-2 p-2 border rounded"
                  rows="4"
                ></textarea>
              </div>

              {/* Error and Success Messages */}
              {errorMessage && (
                <div className="mt-4 text-red-600">{errorMessage}</div>
              )}
              {successMessage && (
                <div className="mt-4 text-green-600">{successMessage}</div>
              )}

              <button
                type="submit"
                className="bg-blue-500 text-white py-2 px-4 rounded hover:bg-blue-600 mt-4"
              >
                Add Staff
              </button>
            </form>
          </div>
        </div>
      </div>
    </BaseLayout>
  );
};

export default AddStaff;
