import React, { useState, useEffect } from "react";
import Modal from "react-modal";
import "tailwindcss/tailwind.css";
import BaseLayout from "../../layouts/BaseLayout";
import axios from "axios"; // Import axios for API calls
import { useSelector } from "react-redux";

Modal.setAppElement("#root");

const AttendanceCalendar = () => {
  const [currentDate, setCurrentDate] = useState(new Date());
  const [dailyAttendance, setDailyAttendance] = useState([]);
  const [apLeave, setApLeave] = useState([]);
  const [selectedDate, setSelectedDate] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [startTime, setStartTime] = useState("09:00");
  const [endTime, setEndTime] = useState("17:00");
  const [dateTimes, setDateTimes] = useState({}); // To store time ranges from the API
  const [savedTimeRanges, setSavedTimeRanges] = useState({}); // To store newly saved time ranges
  const token = useSelector((state) => state.token);

  const today = new Date();
  const monthNames = [
    "Jan",
    "Feb",
    "Mar",
    "Apr",
    "May",
    "Jun",
    "Jul",
    "Aug",
    "Sep",
    "Oct",
    "Nov",
    "Dec",
  ];

  const populateMonthDropdown = () => {
    return monthNames.map((month, index) => (
      <option key={index} value={index}>
        {month}
      </option>
    ));
  };

  const populateYearDropdown = () => {
    const currentYear = today.getFullYear();
    const previousYears = [
      currentYear - 2,
      currentYear - 1,
      currentYear,
      currentYear + 1,
      currentYear + 2,
      currentYear + 3,
      currentYear + 4,
      currentYear + 5,
    ];
    return previousYears.map((year) => (
      <option key={year} value={year}>
        {year}
      </option>
    ));
  };

  useEffect(() => {
    const fetchCheckupDates = async () => {
      try {
        const response = await axios.get("http://localhost:8080/doctor/dates", {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        if (
          response.data.success &&
          Array.isArray(response.data.datesAndTimes)
        ) {
          const fetchedData = {};
          response.data.datesAndTimes.forEach((item) => {
            const dateKey = new Date(item.date).toISOString().split("T")[0];
            const timeRanges = item.timeRanges.map((timeRange) => ({
              startTime: timeRange.startTime,
              endTime: timeRange.endTime,
            }));
            fetchedData[dateKey] = timeRanges;
          });
          setDateTimes(fetchedData);
        } else {
          console.error("Unexpected response structure");
        }
      } catch (error) {
        console.error("Error fetching dates: ", error);
      }
    };

    fetchCheckupDates();
  }, [token]);

  const generateCalendar = (month, year) => {
    const firstDay = new Date(year, month).getDay();
    const daysInMonth = new Date(year, month + 1, 0).getDate();
    const prevMonthDays = new Date(year, month, 0).getDate();
    const offset = firstDay === 0 ? 6 : firstDay - 1;
    const dates = [];

    // Previous month's days
    for (let i = offset; i > 0; i--) {
      dates.push(
        <div
          key={`prev-${i}`}
          className="w-1/7 bg-gray-200 text-gray-500 py-2 text-center"
        >
          {prevMonthDays - i + 1}
        </div>
      );
    }

    // Current month's days
    for (let day = 1; day <= daysInMonth; day++) {
      const dateKey = `${year}-${month + 1}-${day}`; // Adjust month to be 1-indexed
      const hasTimeRanges = dateTimes[dateKey] || savedTimeRanges[dateKey];

      const eventClass = hasTimeRanges
        ? "bg-green-100 border-l-4 border-green-500"
        : "bg-red-100 border-l-4 border-red-500";

      // Combine both time ranges for display
      const timeRangesToDisplay = [
        ...(dateTimes[dateKey] || []),
        ...(savedTimeRanges[dateKey] || []),
      ];

      // Display all time ranges if they exist
      const timeText = timeRangesToDisplay
        ? timeRangesToDisplay
            .map((range) => `${range.startTime} - ${range.endTime}`)
            .join(", ")
        : "";

      dates.push(
        <div
          key={day}
          className={`w-1/7 p-2 text-center h-[100px] flex flex-col justify-center ${eventClass} cursor-pointer`}
          onClick={() => handleDateClick(day, month, year)}
        >
          <div>{day}</div>
          {timeText && <div className="text-sm">{timeText}</div>}{" "}
          {/* Display time ranges */}
        </div>
      );
    }

    return dates;
  };

  const handleTimeSave = async () => {
    if (startTime && endTime && selectedDate) {
      const dateKey = `${selectedDate.getFullYear()}-${
        selectedDate.getMonth() + 1
      }-${selectedDate.getDate()}`; // Adjust month to be 1-indexed

      const payload = {
        date: selectedDate.toISOString().split("T")[0],
        timeRanges: [{ startTime, endTime }],
      };

      try {
        const response = await axios.post(
          "http://localhost:8080/doctor/set-checkup-times",
          payload,
          {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          }
        );
        console.log("API response: ", response.data);

        // Update local state with the newly saved time range
        setSavedTimeRanges((prevRanges) => ({
          ...prevRanges,
          [dateKey]: [...(prevRanges[dateKey] || []), { startTime, endTime }], // Append the new range
        }));

        setIsModalOpen(false); // Close modal after saving
      } catch (error) {
        console.error(
          "Error setting checkup time: ",
          error.response ? error.response.data : error
        );
        alert(
          error.response?.data.message ||
            "Failed to save checkup time. Please try again."
        );
      }
    } else {
      alert("Please select both start and end time");
    }
  };

  return (
    <BaseLayout>
      <div className="w-full p-4 bg-white rounded-lg shadow">
        {/* Calendar Header */}
        <div className="flex justify-between items-center mb-4">
          <h3 className="text-lg font-semibold">Set Future Check-Up Time</h3>
          <div className="flex items-center">
            <button
              className="text-lg"
              onClick={() =>
                setCurrentDate(
                  new Date(
                    currentDate.getFullYear(),
                    currentDate.getMonth() - 1,
                    1
                  )
                )
              }
            >
              &larr;
            </button>
            <button
              className="text-lg ml-2"
              onClick={() =>
                setCurrentDate(
                  new Date(
                    currentDate.getFullYear(),
                    currentDate.getMonth() + 1,
                    1
                  )
                )
              }
            >
              &rarr;
            </button>
            <select
              className="ml-4 border-none"
              value={currentDate.getMonth()}
              onChange={handleMonthChange}
            >
              {populateMonthDropdown()}
            </select>
            <select
              className="ml-2 border-none"
              value={currentDate.getFullYear()}
              onChange={handleYearChange}
            >
              {populateYearDropdown()}
            </select>
          </div>
        </div>

        <div className="grid grid-cols-7 gap-4">
          <div className="text-center bg-gray-800 text-white py-2">Mon</div>
          <div className="text-center bg-gray-800 text-white py-2">Tue</div>
          <div className="text-center bg-gray-800 text-white py-2">Wed</div>
          <div className="text-center bg-gray-800 text-white py-2">Thu</div>
          <div className="text-center bg-gray-800 text-white py-2">Fri</div>
          <div className="text-center bg-gray-800 text-white py-2">Sat</div>
          <div className="text-center bg-gray-800 text-white py-2">Sun</div>
        </div>

        <div className="grid grid-cols-7 gap-4">
          {generateCalendar(currentDate.getMonth(), currentDate.getFullYear())}
        </div>

        <Modal
          isOpen={isModalOpen}
          onRequestClose={() => setIsModalOpen(false)}
        >
          <h2 className="text-lg font-semibold">
            Set Time for {selectedDate && selectedDate.toLocaleDateString()}
          </h2>
          <div>
            <label>Start Time:</label>
            <input
              type="time"
              value={startTime}
              onChange={(e) => setStartTime(e.target.value)}
            />
            <label>End Time:</label>
            <input
              type="time"
              value={endTime}
              onChange={(e) => setEndTime(e.target.value)}
            />
          </div>
          <button onClick={handleTimeSave}>Save</button>
          <button onClick={() => setIsModalOpen(false)}>Close</button>
        </Modal>
      </div>
    </BaseLayout>
  );
};

export default AttendanceCalendar;
